<?phpadd_action('tshirtecommerce_html', 'tshirtecommerce_html_auto_redirect_cart');function tshirtecommerce_html_auto_redirect_cart($values){	if (isset($values['redirect_cart']) && $values['redirect_cart'] == 1)	{		echo '<script type="text/javascript">var auto_redirect_cart = "1";</script>';	}}add_action('tshirtecommerce_setting', 'design_order_detail_setting');function design_order_detail_setting($values){	echo '<tr valign="top">'			.'<th scope="row" class="titledesc">Download file design:</th>'			.'<td class="forminp-text">';	if (isset($values['allow_download']) && $values['allow_download'] == 1)	{		echo '<input type="checkbox" value="1" name="designer[allow_download]" checked="checked"> Allow client download file design.';	}	else	{		echo '<input type="checkbox" value="1" name="designer[allow_download]"> Allow client download file design.';	}		echo '</td></tr>';		echo '<tr valign="top">'			.'<th scope="row" class="titledesc">Redirect to page cart:</th>'			.'<td class="forminp-text">';	if (isset($values['redirect_cart']) && $values['redirect_cart'] == 1)	{		echo '<input type="checkbox" value="1" name="designer[redirect_cart]" checked="checked"> Auto redirect to page cart after client click "Buy Now".';	}	else	{		echo '<input type="checkbox" value="1" name="designer[redirect_cart]"> Auto redirect to page cart after client click "Buy Now".';	}		echo '</td></tr>';}add_action('woocommerce_order_item_meta_end', 'design_order_detail', 1, 30);function design_order_detail($item_id, $item, $order){	$settings = get_option( 'online_designer' );	if ( isset($settings['allow_download']) && $settings['allow_download'] == 1 )	{		$download = true;	}	else	{		$download = false;	}		$data = WC_Abstract_Order::get_item_meta( $item_id, "custom_designer", true );		// product design	echo '<div style="float: right;text-align: left;">';	if (isset($data['design_id']) && $data['design_id']!= '' && $data != null && count($data) > 0)	{		if (isset($data['images']))		{
		$images = json_decode(str_replace('\\', '', $data['images']));			if (count($images))			{				echo '<table><tr>';				foreach($images as $view => $image)				{					echo '<td>';					echo '<img style="width: 100px;" src="'. site_url('tshirtecommerce').'/'.$image .'" with="100">';					if ($download == true)					{						echo '<br /><a target="_blank" href="'.site_url('tshirtecommerce/design.php?key='.$data['design_id'].'&view='.$view).'">Download Design</a>';					}					echo '</td>';				}				echo '</tr></table>';			}						}				if (isset($data['teams']['name']) && count($data['teams']['name']) > 0)		{			echo '<table>'				. 		'<thead>'				. 			'<tr>'				. 				'<th>Name</th>'				. 				'<th>Number</th>'				. 				'<th>Sizes</th>'				. 			'</tr>'				. 		'</thead>'				. 		'<tbody>';				
		for($i=1; $i<=count($data['teams']['name']); $i++ )			{				$size = explode('::', $data['teams']['size'][$i]);				echo 		'<tr>'					.			'<td>'.$data['teams']['name'][$i].'</td>'					.			'<td>'.$data['teams']['number'][$i].'</td>'					.			'<td>'.$size[0].'</td>'					.		'</tr>';			}						echo 		'</tbody></table>';		}						if (isset($data['color_title']))		{			echo '<p>Color: '.$data['color_title'].'</p>';		}				if ($data['options'] != '' && $data['options'] != '[]')		{			if (is_string($data['options']))				$options = json_decode( str_replace('\\"', '"', $data['options']), true);			else				$options = $data['options'];									if (count($options) > 0)			{				foreach($options as $i => $option)				{										if (isset($option['type']) && file_exists( dirname(__FILE__) .'/options/'.$option['type'].'.php' ) )					{						require_once(dirname(__FILE__) .'/options/'.$option['type'].'.php');						continue;					}										if (isset($options[$i]) && isset($options[$i]['value']))					{						if (is_string($options[$i]['value']) && $options[$i]['value'] == '') continue;						if (is_array($options[$i]['value']) && count($options[$i]['value']) == 0) continue;													echo '<dt>'.$options[$i]['name'].': </dt>';												echo '<dd>';						if (is_array($options[$i]['value']))						{														foreach ($options[$i]['value'] as $name => $value)							{								echo $name.'  -  '.$value. '; ';							}						}						else						{							echo $options[$i]['value'];						}						echo '</dd>';					}				}			}		}				// add link edit design		if ($data['design_id'] != 'blank')		{			define('ROOT', ABSPATH .'tshirtecommerce');			define('DS', DIRECTORY_SEPARATOR);			include_once (ROOT .DS. 'includes' .DS. 'functions.php');			$dg = new dg();			$lang = $dg->lang('lang.ini', false);											$product_id = $item['product_id'];			$page = get_page_link($settings['url']);			$link = add_query_arg( array('product_id'=>$product_id, 'cart_id'=>$data['design_id']), $page );						echo '<p><a href="'.$link.'" title="'.$lang['designer_cart_edit_des'].'">'.$lang['designer_cart_edit'].'</a></p>';		}	}	else	{		// get design idea				global $wc_cpdf;				$product_id = $wc_cpdf->get_value($item['product_id'], '_product_id');		if ($product_id != '')		{				$page = get_page_link($settings['url']);							$link = add_query_arg( array('design'=>$product_id, 'parent_id'=>$product->id), $page );						$ids = explode(':', $product_id);			echo '<p>'					.'<a href="'.$link.'" target="_blank" title="click to view design"><strong>View design</strong></a> or Download design:'					.' <a href="'.site_url('tshirtecommerce/design.php?idea=1&key='.$product_id).'&view=front" target="_blank" title="click to view design"><strong>Front</strong></a> - '				.' <a href="'.site_url('tshirtecommerce/design.php?idea=1&key='.$product_id).'&view=back" target="_blank" title="click to view design"><strong>Back</strong></a> - '					.' <a href="'.site_url('tshirtecommerce/design.php?idea=1&key='.$product_id).'&view=left" target="_blank" title="click to view design"><strong>Left</strong></a> - '					.' <a href="'.site_url('tshirtecommerce/design.php?idea=1&key='.$product_id).'&view=right" target="_blank" title="click to view design"><strong>Right</strong></a>'				. '</p>';		}	}	echo '</div>';}?>